packagegui.custom_php_global_functions:
		function sync_package_pfflowd() {
			conf_mount_rw();
            		config_lock();
            		global $config;
            		foreach($config['installedpackages']['pfflowd']['config'] as $cf) {
				if($cf['host'] != "") {
					$start = "\n/sbin/ifconfig pfsync0 up\n";
					$start .= "/usr/local/sbin/pfflowd ";
					$start .= " -n {$cf['host']}";
					if($cf['port'] != "") 
						$start .= ":{$cf['port']}";
					if($cf['direction'] != "") 
						$start .= " -S {$cf['direction']}";
					if($cf['version'] != "") 
						$start .= " -v {$cf['version']}";
					write_rcfile(array(
							"file" => "pfflowd.sh",
							"start" => $start,
							"stop" => "/usr/bin/killall pfflowd"
						)
					);
					restart_service("pfflowd");
					break;
				}
			}
			conf_mount_ro();
			config_unlock();
		}
