packagegui.custom_add_php_command:
	conf_mount_rw();
	config_lock();
	global $config;
	$fout = fopen("/usr/local/etc/ifstated.conf","w");
	if($config['installedpackages']['ifstated']['config'] != "") {
	    fwrite($fout, "# This file was automatically generated by the pfSense
# package management system.

");
	    fwrite($fout, "init-state " . $_POST['initstate'] . "

");
	    fwrite($fout, "carp_up = "((carp0 link up) and (carp1 link up))"
carp_down = "((! carp0 link up) and (! carp1 link up))"
carp_sync= "((carp0 link up and carp1 link up) or ((!carp0 link up) and (!carp1 link up)))"

");
	    fwrite($fout, "net = '( "ping -q -c 1 -t 1 " . $_POST['exttestip'] . " > /dev/null" every 10 and "ping -q -c 1 -t 1 " . $_POST['inttestip'] . " > /dev/null" every 10)'

");
	    fwrite($fout, "peer = '( "ping -q -c 1 -t 1 " . $_POST['pairextip'] . " > /dev/null" every 10 and "ping -q -c 1 -t 1 " . $_POST['pairintip'] . " > /dev/null" every 10)'

");
	    fwrite($fout, "state auto {
if \$carp_up {
set-state primary
}
if \$carp_down {
set-state backup
}
}

");
	    fwrite($fout, "state primary {
init {
run "ifconfig carp0 advskew 10"
run "ifconfig carp1 advskew 10"
}
if ! \$net {
set-state demoted
}
}

state demoted {
init {
run "ifconfig carp0 advskew 254"
run "ifconfig carp1 advskew 254"
}
if \$net {
set-state primary
}
}

");
	    fwrite($fout, "state promoted {
init {
run "ifconfig carp0 advskew 0"
run "ifconfig carp1 advskew 0"
}
if \$peer or ! \$net {
set-state backup
}
}

");
	    fwrite($fout, "state backup {
init {
run "ifconfig carp0 advskew 100"
run "ifconfig carp1 advskew 100"
}
if ! \$carp_sync and \$net and "sleep 5" every 10 {
if (! \$carp_sync) and \$net {
set-state promoted
}
}
}
");
	}
	fclose($fout);
	conf_mount_ro();
	config_unlock();
	mwexec("/usr/bin/killall -HUP ifstated");
